mapper.select:
SELECT USER_ID as userId, PLAN_ID as planId, SUBJECT, SUMMARY, CREATE_TIME as createTime, HIT FROM PLAN
WHERE SUBJECT LIKE #{pattern}
WHERE USER_ID LIKE #{userId}
ORDER BY CREATE_TIME DESC

mapper.update:
UPDATE 
	PLAN
SET 
	SUBJECT = #{subject}, SUMMARY =	#{summary}, CREATE_TIME = #{createTime}
WHERE 
	PLAN_ID = #{planId}

mapper.delete:
DELETE FROM 
	USER_PLAN_LIKE
WHERE 
	PLAN_ID_LIKE = #{planId}

		AND USER_ID_LIKE = #{userId}

DELETE FROM 
	USER_PLAN_BOOKMARK
WHERE 
	PLAN_ID = #{planId}

		AND USER_ID = #{userId}
